function show(d){count=0;for(var e in methods){var f=document.getElementById(e);if((methods[e]&d)!=0){f.style.display="";f.className=(count++%2)?rowColor:altColor}else{f.style.display="none"}}updateTabs(d)}function updateTabs(h){for(var g in tabs){var f=document.getElementById(tabs[g][0]);var e=f.firstChild;if(g==h){f.className=activeTableTab;e.innerHTML=tabs[g][1]}else{f.className=tableTab;e.innerHTML='<a href="javascript:show('+g+');">'+tabs[g][1]+"</a>"}}};